<?phpinclude 'configSubscription.php';if(isset($_GET['cmd'])){	if($_GET['cmd'] == "delete")		deleteSubscription($_GET['id']);	else if($_GET['cmd'] == "register")		registerSubscription();}$result = checkSubscription();if(empty($result)){	echo "None of request exists<br>";	echo '<a href="?cmd=register">Register Subscription</a>';}else{		echo "ID Request: ".$result."<br>";	echo '<a href="?cmd=delete&id='.$result.'">Remove Subscription</a>';}?>