<?phpinclude 'configSubscription.php';if(isset($_GET['cmd'])){	if($_GET['cmd'] == "delete")		deleteSubscription($_GET['id']);	else if($_GET['cmd'] == "register")		registerSubscription();}$result = checkSubscription();if(is_null($result)){	echo "None of request exists<br>";	echo '<a href="?cmd=register">Register Subscription</a>';}else{		echo "ID Request: ".$result->id."<br>";	echo "Callback URL: ".$result->callback_url."<br>";	echo '<a href="?cmd=delete&id='.$result->id.'">Remove Subscription</a>';}?>